{"changed":true,"filter":false,"title":"index.php","tooltip":"/index.php","value":"<?php \nsession_start();\n$host = getenv('IP');\n$username = 'Cduncan';\n$password = 'Damdog_101';\n$dbname = 'DB';\n$conn = new PDO(\"mysql:host=$host;dbname=$dbname;charset=utf8mb4\", $username, $password);\n$users = $conn->query(\"SELECT * FROM users\");\n$userslist = $users->fetchAll(PDO::FETCH_ASSOC);\n$issues = $conn->query(\"SELECT * FROM issues\");\n$issueslist = $issues->fetchAll(PDO::FETCH_ASSOC);\n\n\n# Login and set session variables\nif(isset($_GET['email']) && \nisset($_GET['password'])){\n    foreach ($userslist as $user):\n    if ($user['email']==$_GET['email'] && \n    password_verify($_GET['password'], $user['password'])){\n\n        $_SESSION['email']=$user['email'];\n        $_SESSION['password']=$user['password'];\n        $_SESSION['id']=$user['id'];\n        echo \"true\";\n    }\n    endforeach;\n\n\t}\n\t\n\t\n#Session\nif(isset($_GET['session_id'])){\n    echo $_SESSION['email'];\n}\n\n# Display all users\nif(isset($_GET['display_users'])){\n    foreach ($userslist as $user):\n        $line=$user['id'].','.$user['lastname'] .','.$user['firstname'];\n        echo $line;\n        echo \"<br>\";\n        \n    endforeach;\n\n\t}\n\t\n# Display all issues\nif(isset($_GET['display_issues'])){\n   echo \" <table>\";\n        echo \"<tr>\";\n        echo \"    <th>Title</th>\";\n        echo \"    <th>Type</th>\";\n        echo \"    <th>Status</th>\";\n        echo \"    <th>Assigned To</th>\";\n        echo \"    <th>Created</th>\";\n        echo \"  </tr>\";\n    foreach ($issueslist as $issue):\n        $id=$issue['id'];\n        $title=$issue['title'];\n        $type=$issue['type'];\n        $status=$issue['status'];\n        $assigned_to=$issue['assigned_to'];\n        $created=$issue['created'];\n        echo \"<tr>\";\n        echo \"    <td>#$id $title</td>\";\n        echo \"    <td>$type</td>\";\n        if (strcmp($status,\"OPEN\")==0){\n        echo str_replace('#', '\"',\"    <td id=#open#>$status</th>\");    \n        }elseif(strcmp($status,\"CLOSED\")==0){\n        echo str_replace('#', '\"',\"    <td id=#closed#>$status</th>\");     \n        }elseif(strcmp($status,\"IN PROGRESS\")==0){\n        echo str_replace('#', '\"',\"    <td id=#inprogress#>$status</th>\");     \n        }\n        \n        \n        echo \"    <td>$assigned_to</td>\";\n        echo \"    <td>$created</td>\";\n        echo \"  </tr>\";\n        \n    endforeach;\n        echo \"</table>\";\n}\n\n# Display all open issues\nif(isset($_GET['display_issues_open'])){\n   echo \" <table>\";\n        echo \"<tr>\";\n        echo \"    <th>Title</th>\";\n        echo \"    <th>Type</th>\";\n        echo \"    <th>Status</th>\";\n        echo \"    <th>Assigned To</th>\";\n        echo \"    <th>Created</th>\";\n        echo \"  </tr>\";\n    foreach ($issueslist as $issue):\n        if (strcmp($issue['status'], 'OPEN') == 0){\n        $id=$issue['id'];\n        $title=$issue['title'];\n        $type=$issue['type'];\n        $status=$issue['status'];\n        $assigned_to=$issue['assigned_to'];\n        $created=$issue['created'];\n        echo \"<tr>\";\n        echo \"    <td>$id $title</td>\";\n        echo \"    <td>$type</td>\";\n        echo \"    <td>$status</td>\";\n        echo \"    <td>$assigned_to</td>\";\n        echo \"    <td>$created</td>\";\n        echo \"  </tr>\";\n        }\n    endforeach;\n    echo \"</table>\";\n}\n\n# Display all my tickets\nif(isset($_GET['display_issues_tickets'])){\n   echo \" <table>\";\n        echo \"<tr>\";\n        echo \"    <th>Title</th>\";\n        echo \"    <th>Type</th>\";\n        echo \"    <th>Status</th>\";\n        echo \"    <th>Assigned To</th>\";\n        echo \"    <th>Created</th>\";\n        echo \"  </tr>\";\n    foreach ($issueslist as $issue):\n        if (strcmp($issue['assigned_to'], $_SESSION['email']) == 0){\n        $id=$issue['id'];\n        $title=$issue['title'];\n        $type=$issue['type'];\n        $status=$issue['status'];\n        $assigned_to=$issue['assigned_to'];\n        $created=$issue['created'];\n        echo \"<tr>\";\n        echo \"    <td>$id $title</td>\";\n        echo \"    <td>$type</td>\";\n        echo \"    <td>$status</td>\";\n        echo \"    <td>$assigned_to</td>\";\n        echo \"    <td>$created</td>\";\n        echo \"  </tr>\";\n        }\n    endforeach;\n    echo \"</table>\";\n}\n\n# Add a new user\nif(isset($_GET['Firstname']) && \nisset($_GET['Lastname']) && \nisset($_GET['Password']) && \nisset($_GET['Email'])){\n    $firstname=$_GET['Firstname'];\n    $lastname=$_GET['Lastname'];\n    $password=password_hash($_GET['Password'], PASSWORD_DEFAULT);\n    $email=$_GET['Email'];\n    $date_joined=date(\"Y/m/d\");;\n    $id=rand();\n    $conn->query(str_replace('#', '\"',\"INSERT INTO users (id, firstname, lastname,password,email,date_joined)\n        VALUES (#$id#,#$firstname#, #$lastname#, #$password#,#$email#,#$date_joined#); \"));\n}\n\n# Add a new issue\nif(isset($_GET['title']) && \nisset($_GET['description']) && \nisset($_GET['assignedto']) && \nisset($_GET['type'])&& \nisset($_GET['priority'])){\n    $title=$_GET['title'];\n    $description=$_GET['description'];\n    $assignedto=$_GET['assignedto'];\n    $type=$_GET['type'];\n    $priority=$_GET['priority'];\n    $status=\"OPEN\";\n    $createdBy=$_SESSION['email'];\n    $created=date(\"Y/m/d\");\n    $updated=date(\"Y/m/d\");\n    $id=rand();\n    $conn->query(str_replace('#', '\"',\"INSERT INTO issues \n    VALUES (#$id#,#$title#,#$description#, #$type#, #$priority#,#$status#,#$assignedto#,#$createdBy#,#$created#,#$updated#); \"));\n    \n}\n\n# Log out\nif(isset($_GET['logout'])){\n\t    \n\t    session_unset();\n\t    session_destroy(); \n    \n}\n\n?>\n","undoManager":{"mark":99,"position":100,"stack":[[{"start":{"row":65,"column":29},"end":{"row":65,"column":31},"action":"remove","lines":["th"],"id":553},{"start":{"row":65,"column